Par_Veh.M_F = m_calcu;                                                        % Weight, Unit: kg      
Par_Veh.Cd = wind_coeff;                                                         % Air Resistance Coefficient
Par_Veh.A_F = wind_area;                                                        % Windward Area, Unit: m^2
Par_Veh.Mu = rolling_coeff;                                                       % Rolling Resistance Coefficient
Par_Veh.Grade = slope;                                                         % Rotation Coefficient
Par_Veh.mt2m_f = rotation_converse_coeff-1;
Par_Veh.L = wheel_base;                                                         % Front and back axis length, Unit: m
Par_Veh.a = forward_distance;                                                         % Center line distance of the center of mass to the center of the front axis, Unit: m
Par_Veh.b = Par_Veh.L - Par_Veh.a ;                                        % Center line distance of the center of mass to the center of the rear axle, Unit: m
Par_Veh.hg = hg_calcu;                                                        % Centroid Height, Unit: m
Par_Veh.Coefficient = adhesion_coeff;                                                 % Road adhesion coefficient
%% Brake Parameters
Par_Brake.Beta = (Par_Veh.Coefficient * Par_Veh.hg + Par_Veh.b) / Par_Veh.L; % Braking force distribution
Par_Brake.Brake_Speed_Limit = 10;                                            % When Speed is lower than 10 km/h, mechanical braking 
Par_Brake.Elec_Brake_Dec_Limit = -2;                                         % When Dec is lower than -2 m/s^2, mechanical braking 

